	else if(v_POLY_switch1_index == 7){v_POLY_switch1_val = vec3(0.0, 0.0, 0.0);}
	else if(v_POLY_switch1_index == 8){v_POLY_switch1_val = vec3(0.0, 0.0, 0.0);}
	else if(v_POLY_switch1_index == 9){v_POLY_switch1_val = vec3(0.0, 0.0, 0.0);}
	else if(v_POLY_switch1_index == 10){v_POLY_switch1_val = vec3(0.0, 0.0, 0.0);}

	// /MAT/mesh_basic_builder_ARCS/and1
	bool v_POLY_and1_and = v_POLY_compare3_val && v_POLY_compare4_val;

	// /MAT/mesh_basic_builder_ARCS/mix2
