	v_POLY_twoWaySwitch1_val = 1.0;
	} else {
	v_POLY_twoWaySwitch1_val = 0.0;
	}

	// /MAT/mesh_basic_builder_ARCS/rgb_to_hsv1
	vec3 v_POLY_rgb_to_hsv1_hsv = rgb2hsv(v_POLY_mix2_mix);

	// /MAT/mesh_basic_builder_ARCS/null1
