	float v_POLY_null_alpha_val = v_POLY_null1_val;

	// /MAT/mesh_basic_builder_ARCS/multAdd3
	float v_POLY_multAdd3_val = (0.34*(v_POLY_vec3_to_float1_y + 0.0)) + 0.0;

	// /MAT/mesh_basic_builder_ARCS/float_to_vec3_1
	vec3 v_POLY_float_to_vec3_1_vec3 = vec3(v_POLY_vec3_to_float1_x, v_POLY_multAdd3_val, v_POLY_vec3_to_float1_z);

	// /MAT/mesh_basic_builder_ARCS/hsv_to_rgb1
